mapper.insert:
INSERT INTO CART (USER_CODE, PRODUCT_CODE, ORDER_QUANTITY, CART_CODE) VALUES ( #{user_code}, #{product_code}, #{order_quantity},
fnCode('cart')
#{cart_code}
)

mapper.delete:
  		DELETE FROM CART 
		WHERE USER_CODE = #{user_code}
		AND PRODUCT_CODE = #{product_code}
		AND CART_CODE = #{cart_code}
		AND ORDER_QUANTITY = #{order_quantity}
