mapper.select:
SELECT COUNT(*)
FROM USER
WHERE USER_ID = #{value}

SELECT COUNT(*)
FROM USER
WHERE USER_ID = #{user_id}
    AND USER_PASS = #{user_pass}

SELECT USER_ID
FROM USER
WHERE USER_NAME = #{user_name}
AND USER_TEL = #{user_tel}

SELECT COUNT(*)
FROM USER
WHERE USER_ID = #{user_id}
AND USER_NAME = #{user_name}
AND USER_TEL = #{user_tel}

mapper.update:
UPDATE USER
SET USER_PASS = #{new_password}
WHERE USER_ID = #{user_id}
