mapper.select:
SELECT * FROM ( SELECT P.*, U.USER_POINT, O.PAYMENT_STATE, O.TOTAL_PRICE, CASE WHEN P.POINT_CHANGE >= 0 THEN '적립' ELSE '사용' END AS POINT_SECTION, @ROWNUM:=@ROWNUM+1 AS ROWNUM FROM POINTLOG AS P JOIN USER AS U ON U.USER_CODE = P.USER_CODE JOIN ORDERS AS O ON P.ORDER_CODE = O.ORDER_CODE, (SELECT @ROWNUM:=0) AS R WHERE U.USER_CODE = #{user_code}
AND DATE_FORMAT(P.POINT_JOINDATE, '%Y%m%d') BETWEEN DATE_FORMAT(#{startDate}, '%Y%m%d') AND DATE_FORMAT(#{endDate}, '%Y%m%d')
ORDER BY P.POINT_JOINDATE ASC ) AS T ORDER BY ROWNUM DESC;

mapper.update:
UPDATE USER 
SET USER_POINT  = (
    SELECT SUM(POINT_CHANGE) 
    FROM POINTLOG 
    WHERE USER_CODE = #{user_code}
) 
WHERE USER_CODE = #{user_code}

mapper.select:
SELECT SUM(POINT_CHANGE) 
