mapper.select:
SELECT U.USER_NAME AS NAME, F.PRODUCT_NAME AS MENU, C.ORDER_QUANTITY AS QUANTITY, SUM(F.PRODUCT_PRICE * C.ORDER_QUANTITY) AS SALES, DATE_FORMAT(STR_TO_DATE(O.PAYMENT_DATE, '%Y%m%d'),'%Y-%m-%d') AS TIME FROM ORDERS O INNER JOIN CART C ON O.CART_CODE = C.CART_CODE INNER JOIN FOOD F ON C.PRODUCT_CODE = F.PRODUCT_CODE INNER JOIN USER U ON C.USER_CODE = U.USER_CODE WHERE 1=1
AND U.USER_NAME = #{NAME}
AND F.PRODUCT_NAME = #{MENU}
AND SUBSTRING(O.PAYMENT_DATE,5,2) = #{MONTH}
AND SUBSTRING(O.PAYMENT_DATE,7,2) = #{DAY}
AND O.PAYMENT_DATE BETWEEN #{DATE1} AND #{DATE2}
GROUP BY U.USER_NAME, F.PRODUCT_NAME, O.PAYMENT_DATE ORDER BY O.PAYMENT_DATE DESC;

SELECT U.USER_NAME AS NAME, B.BOOK_NAME AS BOOK, CASE WHEN #{MONTH} IS NOT NULL AND #{MONTH} != '' AND SUBSTRING(R.BORROW_DATE, 5, 2) = #{MONTH} THEN B.BOOK_PRICE WHEN #{MONTH} IS NULL OR #{MONTH} = '' THEN B.BOOK_PRICE ELSE 0 END AS SALES, CASE WHEN #{MONTH} IS NOT NULL AND #{MONTH} != '' AND SUBSTRING(R.RETURN_DATE, 5, 2) = #{MONTH} THEN R.MONEY WHEN #{MONTH} IS NULL OR #{MONTH} = '' THEN R.MONEY ELSE 0 END AS LATE_FEE, DATE_FORMAT(STR_TO_DATE(R.BORROW_DATE, '%Y%m%d'), '%Y-%m-%d') AS BORROW_DATE, DATE_FORMAT(STR_TO_DATE(R.RETURN_DATE, '%Y%m%d'), '%Y-%m-%d') AS RETURN_DATE FROM BORROW R JOIN BOOK B ON R.BOOK_CODE = B.BOOK_CODE JOIN USER U ON R.USER_CODE = U.USER_CODE WHERE 1=1
AND U.USER_NAME = #{NAME}
AND B.BOOK_NAME = #{BOOK}
AND (SUBSTRING(R.BORROW_DATE, 5, 2) = #{MONTH} OR SUBSTRING(R.RETURN_DATE, 5, 2) = #{MONTH})
AND SUBSTRING(R.BORROW_DATE, 7, 2) = #{DAY}
AND R.BORROW_DATE BETWEEN #{DATE1} AND #{DATE2}
ORDER BY R.BORROW_DATE DESC;
